class Menu < Chingu::GameState  def setup        ## (:title => "Enter IP or Hostname of Netsnakeserver", :callback => :got_server)        #    # TODO:    #    # Chingu::GameStates::EnterName.new sätter $window.inside_state    # vilket fuckar upp SimpleMenu.create precis efteråt...     # "Configure" => Chingu::GameStates::EnterName.new,    #    #Text.size = 40    menu_items = {  "Start Game" => Client,                     "Configure" => EnterIP,                    "HighScores" => :exit,                     "Exit" => :exit                 }    SimpleMenu.create(:spacing => 40, :y => 200, :menu_items => menu_items, :size => 30)  end    def got_server(string)    $window.server = string  endendclass EnterIP < Chingu::GameState  def setup    @textinput = TextInput.new    $window.text_input = @textinput    on_input([:enter, :return], :done)        @title = Text.create("Please enter IP or HOST of server:")    @ip = Text.create("", :x => @title.width)  end    def update    @ip.text = @textinput.text  end    def done    $window.server = @ip.text  endend